$(document).ready(function () {
    $(".section.section-header .select ").on("click", ".btn", function (event) {
        event.preventDefault();
        var id = '.section-bookit',
            top = $(id).offset().top;
        $('body,html').animate({scrollTop: top}, 1500);
    });
    $(".important-to-know-content .discover").on("click", ".link-inline", function (event) {
        event.preventDefault();
        var id = '.section-bookit',
            top = $(id).offset().top;
        $('body,html').animate({scrollTop: top}, 1500);
    });
    $(".section.section-select").on("click", ".link-inline", function (event) {
        event.preventDefault();
        var id = '.section-bookit',
            top = $(id).offset().top;
        $('body,html').animate({scrollTop: top}, 1500);
    });
    $(".section.section-better-conditions").on("click", ".link-inline", function (event) {
        event.preventDefault();
        var id = '.section-bookit',
            top = $(id).offset().top;
        $('body,html').animate({scrollTop: top}, 1500);
    });
    $(document).on('submit', 'form', function (e) {
        e.preventDefault();
        form = $(this);

        $.ajax({
            url: 'ajax.php',
            data: form.serialize(),
            type: "post",
            dataType: "json",
            success: function (resp) {
                $(form).find('input', 'button').remove();
                if (resp.transaction_id) {
                    ga('require', 'ecommerce');
                    ga('ecommerce:addTransaction', {
                        'id': resp.transaction_id,
                        'affiliation': 'Franchises Orders',
                        'revenue': resp.franchise_invest,
                        'shipping': '0',
                        'tax': '0'
                    });
                    ga('ecommerce:addItem', {
                        'id': resp.transaction_id,
                        'name': resp.franchise_name,
                        'sku': resp.order_article,
                        'category': resp.order_id,
                        'price': resp.franchise_invest,
                        'quantity': '1'

                    });
                    ga('ecommerce:send');
                }
                $('.section.section-bookit h2').html('Спасибо! Ваша заявка успешно отправлена!');
                form.remove();
            }
        });
    });
});
